'use strict';

var templateLoader = {
    cache : [],

    init : function () {
        templateLoader.cache = [];
    },

    isCached : function (path) {
        if (templateLoader.cache[path] !== undefined) {
            return (true);
        }

        return(false);
    },


    load : function (path, callback) {

        if (templateLoader.isCached(path) === true) {
            callback(true);
        } else {
            var tmplLoader = $.get(path)
                .success(function(result){
                    //On success, Add templates to DOM (assumes file only has template definitions)
                    $("body").append(result);
                   templateLoader.cache[path] = true;
                    callback(true);
                })
                .error(function(error){
                    ux.notify("Error Loading template " + path, error);
                    callback(false);
                });

            tmplLoader.complete(function(){
                //Publish an event that indicates when a template is done loading
                templateLoader.cache[path] = true;
                callback(true);
            });
        }

    },


    loadHtml : function (selector, path, callback) {
        var sel = '#' + selector;
        var $sel = $(sel);


        if ($sel.length > 0) {
            return callback (true);
        }

        $.ajax({
            url: path,
            dataType: "html"})
        .done(function(data) {
                $('#app').append(data);
                callback (true);
            })
        .fail(function( jqXHR, textStatus ) {
            ux.notify( "Request failed: " + textStatus, "error");
            callback(false);
        });

    }

};

